    }

    promptTags(function (tags, fromCache) {
      if (!tags || !tags.length) { releaseLockSoon(); return; }

      // Si esta invocaci√≥n vino inmediatamente despu√©s de otra (fromCache === true),
      // significa que es un duplicado -&gt; no reaplicar para evitar duplicados visibles.
      if (fromCache) { releaseLockSoon(); return; }

      // ====== Aplicaci√≥n ======
      var count = 0;
      for (var i = 0; i &lt; items.length; i++) {
