      var msg = &#039;Etiqueta(s) &quot;&#039; + tags.join(&quot;, &quot;) + &#039;&quot; agregada(s) a &#039; + count + &quot; √≠tem(s).&quot;;
      if (ZoteroPane &amp;&amp; ZoteroPane.showNotification) ZoteroPane.showNotification(msg);
